import React, { useState, useEffect } from 'react';
import { categoryAPI, transactionAPI } from '../services/api';
import { Plus, X } from 'lucide-react';

const PAYMENT_METHODS = [
  { value: 'UPI', label: 'UPI Apps' },
  { value: 'CREDIT_CARD', label: 'Credit Card' },
  { value: 'DEBIT_CARD', label: 'Debit Card' },
  { value: 'WALLET', label: 'Digital Wallet' },
  { value: 'CASH', label: 'Cash' },
  { value: 'NET_BANKING', label: 'Net Banking' },
  { value: 'SUBSCRIPTION', label: 'Subscription' },
];

function TransactionForm({ onTransactionAdded, editingTransaction, onCancelEdit }) {
  const [categories, setCategories] = useState([]);

  const [formData, setFormData] = useState({
    description: '',
    amount: '',
    transactionDate: new Date().toISOString().split('T')[0],
    paymentMethod: 'UPI',
    paymentDetails: '',
    categoryId: null,
    notes: '',
    isRecurring: false,
  });

  useEffect(() => {
    loadCategories();
  }, []);

  useEffect(() => {
    if (editingTransaction) {
      setFormData({
        description: editingTransaction.description || '',
        amount: editingTransaction.amount,
        transactionDate: editingTransaction.transactionDate,
        paymentMethod: editingTransaction.paymentMethod,
        paymentDetails: editingTransaction.paymentDetails || '',
        categoryId: editingTransaction.categoryId,
        notes: editingTransaction.notes || '',
        isRecurring: editingTransaction.isRecurring || false,
      });
    }
  }, [editingTransaction]);

  const loadCategories = async () => {
    try {
      const res = await categoryAPI.getAll();
      setCategories(res.data);

      if (res.data.length > 0) {
        setFormData(prev => ({
          ...prev,
          categoryId: prev.categoryId ?? res.data[0].id
        }));
      }
    } catch (err) {
      console.error("Category load failed", err);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!formData.categoryId) {
      alert("Please select a category first");
      return;
    }

    try {
      const data = {
        ...formData,
        amount: Number(formData.amount),
        categoryId: Number(formData.categoryId),
      };

      if (editingTransaction) {
        await transactionAPI.update(editingTransaction.id, data);
      } else {
        await transactionAPI.create(data);
      }

      setFormData({
        description: '',
        amount: '',
        transactionDate: new Date().toISOString().split('T')[0],
        paymentMethod: 'UPI',
        paymentDetails: '',
        categoryId: categories.length ? categories[0].id : null,
        notes: '',
        isRecurring: false,
      });

      onTransactionAdded();
      if (onCancelEdit) onCancelEdit();

    } catch (error) {
      console.error("Save error:", error);
      alert(error.response?.data?.message || "Failed to save transaction");
    }
  };

  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;

    setFormData(prev => ({
      ...prev,
      [name]: type === "checkbox" ? checked : value
    }));
  };

  return (
    <div className="card">
      <div style={{ display: 'flex', justifyContent: 'space-between' }}>
        <h2>{editingTransaction ? "Edit Transaction" : "Add Transaction"}</h2>
        {editingTransaction && (
          <button onClick={onCancelEdit} className="btn btn-secondary btn-small">
            <X size={16} /> Cancel
          </button>
        )}
      </div>

      <form onSubmit={handleSubmit}>

        <input name="description" value={formData.description} onChange={handleChange} placeholder="Description" required />

        <input type="number" name="amount" value={formData.amount} onChange={handleChange} placeholder="Amount" required />

        <input type="date" name="transactionDate" value={formData.transactionDate} onChange={handleChange} required />

        <select name="paymentMethod" value={formData.paymentMethod} onChange={handleChange}>
          {PAYMENT_METHODS.map(m => (
            <option key={m.value} value={m.value}>{m.label}</option>
          ))}
        </select>

        <input name="paymentDetails" value={formData.paymentDetails} onChange={handleChange} placeholder="Payment Details" />

        <select name="categoryId" value={formData.categoryId || ""} onChange={handleChange} required>
          <option value="">Select Category</option>
          {categories.map(c => (
            <option key={c.id} value={c.id}>{c.name}</option>
          ))}
        </select>

        <textarea name="notes" value={formData.notes} onChange={handleChange} placeholder="Notes" />

        <label>
          <input type="checkbox" name="isRecurring" checked={formData.isRecurring} onChange={handleChange} />
          Recurring
        </label>

        <button type="submit" className="btn btn-primary">
          <Plus size={18} /> {editingTransaction ? "Update" : "Add"}
        </button>

      </form>
    </div>
  );
}

export default TransactionForm;
