import axios from 'axios';

const API_BASE_URL = 'http://localhost:8080/api';

const api = axios.create({
  baseURL: API_BASE_URL,
  headers: {
    'Content-Type': 'application/json',
  },
});

// Category API
export const categoryAPI = {
  getAll: () => api.get('/categories'),
  getById: (id) => api.get(`/categories/${id}`),
  create: (data) => api.post('/categories', data),
  update: (id, data) => api.put(`/categories/${id}`, data),
  delete: (id) => api.delete(`/categories/${id}`),
};

// Transaction API
export const transactionAPI = {
  getAll: () => api.get('/transactions'),
  getById: (id) => api.get(`/transactions/${id}`),
  getByDateRange: (startDate, endDate) => 
    api.get('/transactions/date-range', { params: { startDate, endDate } }),
  getByCategory: (categoryId) => api.get(`/transactions/category/${categoryId}`),
  getByPaymentMethod: (paymentMethod) => 
    api.get(`/transactions/payment-method/${paymentMethod}`),
  create: (data) => api.post('/transactions', data),
  update: (id, data) => api.put(`/transactions/${id}`, data),
  delete: (id) => api.delete(`/transactions/${id}`),
  getSummary: (startDate, endDate) => 
    api.get('/transactions/summary', { params: { startDate, endDate } }),
};

export default api;
